$(document).ready(function(){$.getJSON("vacancies.json",function(a){for(var c=0;c<a.vacancies.length;c++)a.vacancies[c].id,$(".vacancies-head").append('<div id="'+a.vacancies[c].id+'" class="row padding-top padding-bottom vacancies-card"><div class="col-lg-3 col-sm-3 col-12"><img class="vacancies-img" src="'+a.vacancies[c].img+'"></div><div class="col-lg-9 col-sm-9 col-12"><div class="vacancies-title">'+a.vacancies[c].title+'</div><hr class="vacancies-hr"><div class="vacancies-text">'+a.vacancies[c].shortDescription+'</div></div><div class="col-sm-9 offset-sm-3 col-12"><div class="text-center"><a href="'+a.vacancies[c].formLink+'" target="_blank" style="color:white!important;"><div class="btn my-btn mr-2"><strong>Apply Now</strong></div></a><div class="btn my-btn vacan-btn"  data-vacanID="'+a.vacancies[c].id+'" data-vacanTitle="'+a.vacancies[c].pageName+'"><strong>Read more</strong></div></div></div></div>')})}),$(document).on("click",".vacan-btn",function(){console.log($(this).attr("data-vacanTitle")),window.location=window.location.href.replace("FINA-Careers",$(this).attr("data-vacanTitle"))});